class Solution {public:    int maxWidthOfVerticalArea(vector<vector<int>>& points) {        sort(points.begin(),points.end(),greater());        int n = points.size();        int result = 0;        for(int i = 0; i < n - 1; i++) {           int temp = (points[i][0] - points[i+1][0]);           if(temp > result)            result = temp;        }        return result;    }};